import React from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const DashboardPage = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    logout();
    navigate('/login');
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Welcome, {user?.username}</h2>
        <button className="btn btn-outline-danger" onClick={handleLogout}>Logout</button>
      </div>

      <p><strong>Role:</strong> {user?.role}</p>

      <div className="row mt-4">
        <div className="col-md-4 mb-3">
          <button className="btn btn-primary w-100" onClick={() => navigate('/sow')}>
            SoW & Project Creation
          </button>
        </div>
        <div className="col-md-4 mb-3">
          <button className="btn btn-primary w-100" onClick={() => navigate('/allocate')}>
            Resource Allocation
          </button>
        </div>
        <div className="col-md-4 mb-3">
          <button className="btn btn-primary w-100" onClick={() => navigate('/reports')}>
            Reports & Dashboards
          </button>
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
